import React, { Component } from "react";
import RadioForm, {
  RadioButton,
  RadioButtonInput,
  RadioButtonLabel,
} from "react-native-simple-radio-button";
import { Button } from "./button/index";
import { CheckBox } from "react-native-elements";
import {
  View,
  Text,
  Picker,
  StyleSheet,
  Image,
  TouchableOpacity,
  TextInput,
  Dimensions,
  ActivityIndicator,
  BackHandler,
} from "react-native";
import styles from "./styles1/styles1";
import Constants from "expo-constants";
import * as ImagePicker from "expo-image-picker";
import { LinearGradient } from "expo-linear-gradient";
import * as Font from "expo-font";
import Icon from "./icons";
var disp = [];
import firebase from "firebase";
var screenHeight = Math.round(Dimensions.get("window").height) / 100;
var screenWidth = Math.round(Dimensions.get("window").width) / 100;
var firebaseConfig = {
  apiKey: "AIzaSyAfGN94rWhA55dceve-ab5R5nEL6o4xXeg",
  authDomain: "new1-930be.firebaseapp.com",
  databaseURL: "https://new1-930be.firebaseio.com",
  projectId: "new1-930be",
  storageBucket: "new1-930be.appspot.com",
  messagingSenderId: "332990256430",
  appId: "1:332990256430:web:640a6413492c34bf2a96bf",
  measurementId: "G-SBPS6449GM",
};
var radio_props = [
  { label: "Male    ", value: "Male" },
  { label: "Female", value: "Female" },
];
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}
var newPassword, userName, email, user, password, city, state, uploadUrl, photo;
export default class AddSkill extends Component {
  state = {
    count: 0,
    checked: true,
    skill0: "",
    skill1: "",
    skill2: "",
    skill3: "",
    specific0: "",
    specific1: "",
    specific2: "",
    specific3: "",
    price0: "",
    price1: "",
    price2: "",
    price3: "",
    lesson0: "",
    lesson1: "",
    lesson2: "",
    lesson3: "",
  };

  render() {
    if (true) {
      return (
        <View style={{ backgroundColor: "#F1EEFc", height: "100%" }}>
          <LinearGradient
            colors={["#fc0f84", "#020cab"]}
            start={{ x: 1, y: 0 }}
            end={{ x: 0, y: 1 }}
            style={{ flex: 1 }}
          >
            <View style={{ height: "100%" }}>
              <View
                style={{
                  width: "70%",
                  backgroundColor: "white",
                  alignSelf: "center",
                  borderRadius: 22,
                }}
              >
                <View style={styles.container}>
                  <Text style={styles.text}>What Do You Want To Learn</Text>
                  <Picker
                    style={{
                      height: 50,
                      width: this.state.width,
                      marginVertical: 20,
                      alignSelf: "center",
                      color: "black",
                      borderBottomWidth: 1.5,
                      borderColor: "#D4E6F1",
                      borderBottomColor: "black",
                      backgroundColor: "#D4E6F1",
                      paddingLeft: 220,
                    }}
                    selectedValue={this.state.state}
                    onValueChange={this.handleValueChange}
                    mode="dialog"
                    textStyle={styles.pickerText}
                  >
                    <Picker.Item label="Enter State" value="" />
                    <Picker.Item label="Delhi" value="Delhi" />
                    <Picker.Item label="Chhattisgarh" value="Chhattisgarh" />
                    <Picker.Item label="Goa" value="Goa" />
                    <Picker.Item label="Gujarat" value="Gujarat" />
                    <Picker.Item label="Haryana" value="Haryana" />
                    <Picker.Item label="Jharkhand" value="Jharkhand" />
                    <Picker.Item label="Karnataka" value="Karnataka" />
                    <Picker.Item label="Kerala" value="Kerala" />
                    <Picker.Item
                      label="Madhya Pradesh"
                      value="Madhya Pradesh"
                    />
                    <Picker.Item label="Maharashtra" value="Maharashtra" />
                    <Picker.Item label="Punjab" value="Punjab" />
                    <Picker.Item label="Rajasthan" value="Rajasthan" />
                    <Picker.Item label="Tamil Nadu" value="Tamil Nadu" />
                    <Picker.Item label="Uttar Pradesh" value="Uttar Pradesh" />
                  </Picker>
                  <Picker
                    style={{
                      height: 50,
                      width: this.state.width,
                      marginVertical: 12,
                      alignSelf: "center",
                      color: "black",
                      borderBottomWidth: 1.5,
                      borderColor: "#D4E6F1",
                      borderBottomColor: "black",
                      backgroundColor: "#D4E6F1",
                      paddingLeft: 225,
                    }}
                    onValueChange={(itemValue) => this.updatecity(itemValue)}
                    selectedValue={this.state.city}
                    mode="dialog"
                    textStyle={styles.pickerText}
                  >
                    {this.getcityes()}
                  </Picker>

                  <Picker
                    style={{
                      height: 50,
                      width: this.state.width,
                      marginVertical: 20,
                      alignSelf: "center",
                      color: "black",
                      borderBottomWidth: 1.5,
                      borderColor: "#D4E6F1",
                      borderBottomColor: "black",
                      backgroundColor: "#D4E6F1",
                      paddingLeft: 230,
                    }}
                    selectedValue={this.state.skill}
                    onValueChange={this.updateSkill}
                    textStyle={{ width: screenWidth * 100, color: "white" }}
                    mode="dialog"
                  >
                    <Picker.Item label="Select Skill: " value="" />
                    <Picker.Item label="No Skill" value="student" />
                    <Picker.Item label="Photography" value="Photography" />
                    <Picker.Item label="Videography" value="Videography" />
                    <Picker.Item label="Photoshop" value="Photoshop" />
                    <Picker.Item label="Animation" value="Animation" />
                    <Picker.Item
                      label="Digital Marketing"
                      value="Digital Marketing"
                    />
                    <Picker.Item label="Buisness" value="Buisness" />
                    <Picker.Item
                      label="Musical Instrument"
                      value="Musical Instrument"
                    />
                    <Picker.Item label="Cooking" value="Cooking" />
                    <Picker.Item label="Singing" value="Singing" />
                    <Picker.Item label="Drawing" value="Drawing" />
                    <Picker.Item label="New Language" value="New Language" />
                    <Picker.Item
                      label="Computer Language"
                      value="Computer Language"
