import React, { Component, useState } from "react";
import {
  ScrollView,
  LinearGradient,
  TextInput,
  StyleSheet,
  Text,
  TouchableOpacity,
  TouchableHighlight,
  View,
  ClippingRectangle,
} from "react-native";
import { Dimensions } from "react-native";
var screenWidth = Math.round(Dimensions.get("window").width) / 100;
var screenHeight = Math.round(Dimensions.get("window").height) / 100;
export default class YourApp extends Component {
  render() {
    return (
      <LinearGradient
        colors={["#09C6F9", "#045DE9"]}
        start={{ x: 1, y: 0 }}
        end={{ x: 0, y: 1 }}
        style={{ flex: 1 }}
      >
        <ScrollView style={styles.centeredView}>
          <Text style={styles.bookingDetails}>Booking Details</Text>
          <ScrollView style={styles.modalView}>
            <Text style={styles.lessonSummary}>Lesson Summary</Text>
            <View style={{ flexDirection: "row" }}>
              <Text style={styles.modalText}> Tutor Name: </Text>
              <Text style={styles.right}>Hetan Thakkar </Text>
            </View>
            <View style={{ flexDirection: "row" }}>
              <Text style={styles.modalText}>Lesson Length: </Text>
              <Text style={styles.right}>45 minutes </Text>
            </View>
            <View style={{ flexDirection: "row" }}>
              <Text style={styles.modalText}>Lesson Name: </Text>
              <Text style={styles.right}>React Native </Text>
            </View>
            <View
              style={{
                borderBottomColor: "grey",
                borderBottomWidth: 1,
                marginTop: screenHeight * 3,
              }}
            />
            <Text style={styles.price}>Price Summary </Text>
            <View style={{ flexDirection: "row" }}>
              <Text style={styles.modalText}>Lesson Price:</Text>
              <Text style={styles.lessonPrice1}>$300 </Text>
            </View>
            <Text style={styles.modalText}>Apply Coupon Code </Text>
            <TextInput style={styles.textInput}></TextInput>
            <View style={{ flexDirection: "row" }}>
              <TouchableOpacity
                style={{ ...styles.coupon, backgroundColor: "#2196F3" }}
                onPress={() => {
                  console.log(modalVisible);
                }}
              >
                <Text style={styles.textCoupon}>Apply Coupon</Text>
              </TouchableOpacity>
              <Text style={styles.applied}> Applied !</Text>
            </View>
            <View style={{ flexDirection: "row" }}>
              <Text style={styles.modalText}>Deducted Price: </Text>
              <Text style={styles.right}>- $100 </Text>
            </View>
            <View
              style={{
                borderBottomColor: "grey",
                borderBottomWidth: 1,
                marginTop: screenHeight * 3,
              }}
            />

            <View style={{ flexDirection: "row" }}>
              <Text style={styles.totalText}>Total : </Text>
              <Text style={styles.total}>$ 200 </Text>
            </View>
          </ScrollView>
          <TouchableHighlight
            style={styles.pay}
            onPress={() => {
              setModalVisible(true);
            }}
          >
            <Text style={styles.textStyle}>Proceed to Pay</Text>
          </TouchableHighlight>
        </ScrollView>
      </LinearGradient>
    );
  }
}

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    alignSelf: "center",
    marginTop: screenHeight * 7,
    width: screenWidth * 40,
    height: screenHeight,
  },
  modalView: {
    alignSelf: "center",
    backgroundColor: "white",
    borderRadius: 20,
    borderWidth: 0.9,
    padding: screenHeight * 4,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.95,
    shadowRadius: 10,
    elevation: 8,
  },
  pay: {
    backgroundColor: "#fc0f84",
    borderRadius: 20,
    padding: 10,
    width: screenWidth * 60,
    height: screenHeight * 7,
    alignSelf: "center",
    marginTop: screenHeight * 2,
  },
  applied: {
    marginTop: screenHeight * 2,
    fontSize: 16,
    color: "green",
    marginLeft: screenWidth * 2,
  },
  openButton: {
    backgroundColor: "#F194FF",
    borderRadius: 20,
    padding: 10,
    width: screenWidth * 70,
    height: screenHeight * 6,
    alignSelf: "center",
    marginTop: screenHeight * 5,
  },

  coupon: {
    backgroundColor: "#F194FF",
    borderRadius: 5,
    width: screenWidth * 25,
    height: screenHeight * 4,
    marginTop: screenHeight * 2,
  },
  textStyle: {
    color: "white",
    fontSize: 20,
    fontWeight: "bold",
    textAlignVertical: "center",
    textAlign: "center",
  },
  bookingDetails: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 28,
    marginBottom: screenHeight * 2,
  },
  textCoupon: {
    color: "white",
    fontSize: 15,
    fontWeight: "bold",
    textAlign: "center",
    textAlignVertical: "center",
  },
  textInput: {
    borderWidth: 1.5,
    borderRadius: 10,
    paddingLeft: 10,
    fontSize: 17,
    marginTop: screenHeight * 2,
  },
  modalText: {
    marginTop: screenHeight * 2,
    textAlignVertical: "center",
    fontSize: 18,
  },

  totalText: {
    marginTop: screenHeight * 2,
    marginTop: screenHeight * 2,
    fontSize: 25,
    fontWeight: "bold",
  },
  right: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 2,
    fontSize: 18,
  },

  lessonPrice: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 5,
    fontSize: 18,
  },

  lessonPrice1: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 3,
    fontSize: 18,
  },
  total: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 15,
    fontSize: 20,
  },
  lessonSummary: {
    fontWeight: "bold",
    fontSize: 18,
    marginTop: screenHeight * -0.5,
    marginBottom: screenHeight,
  },
  price: {
    marginTop: screenHeight * 3,
    fontSize: 18,
    fontWeight: "bold",
  },
});
