import React, { Component } from "react";
import {
  View,
  Text,
  SafeAreaView,
  TextInput,
  Platform,
  StatusBar,
  ScrollView,
  Image,
  Dimensions,
} from "react-native";
import { LinearGradient } from "expo-linear-gradient";
import Icon from "react-native-vector-icons/Ionicons";
import Home from "./home";
const { height, width } = Dimensions.get("window");
var screenHeight = Math.round(Dimensions.get("window").height) / 100;
var specifics;
export default class cat extends Component {
  state = {
    skill: "coding",
    specifics: [],
  };
  componentDidMount() {
    if (this.state.skill == "coding")
      specifics = [
        {
          name: "FrontEnd Development",
          image: "https://img-a.udemycdn.com/course/750x422/2314744_c707.jpg",
          price: 30,
        },
        {
          name: "BackEnd Development",
          image:
            "https://www.thebalancecareers.com/thmb/934mZp4W5j4MSCHusNcXGY0MXI8=/1500x1000/filters:fill(auto,1)/backenddeveloper-2502825a14ff440eb775dc4244e7ed4d.png",
          price: 30,
        },
        {
          name: "Web Development",
          image:
            "https://images.pexels.com/photos/1108099/pexels-photo-1108099.jpeg",
          price: 30,
        },
        {
          name: "Mobile Development",
          image:
            "https://images.pexels.com/photos/1108099/pexels-photo-1108099.jpeg",
          price: 30,
        },
        {
          name: "Interview Prep",
          image:
            "https://images.pexels.com/photos/1108099/pexels-photo-1108099.jpeg",
          price: 30,
        },
        {
          name: "Java",
          image:
            "https://images.pexels.com/photos/1108099/pexels-photo-1108099.jpeg",
          price: 30,
        },
        {
          name: "JavaScript",
          image:
            "https://images.pexels.com/photos/1108099/pexels-photo-1108099.jpeg",
          price: 30,
        },
        {
          name: "Python",
          image:
            "https://images.pexels.com/photos/1108099/pexels-photo-1108099.jpeg",
          price: 30,
        },
        // ,
        // "BackEnd Development",
        // "Mobile Development",
        // "Web Development",
        // "Interview Prep",
        // "Java",
        // "JavaScript",
        // "Python",
      ];
    this.setState({ specifics });
  }
  display = () => {
    return this.state.specifics.map((value) => (
      <Home
        width={width}
        name={value.name}
        image={value.image}
        type="PRIVATE ROOM - 2 BEDS"
        price={value.price}
        rating={4}
      />
    ));
  };
  render() {
    return (
      <LinearGradient
        colors={["#09C6F9", "#045DE9"]}
        start={{ x: 1, y: 0 }}
        end={{ x: 0, y: 1 }}
        style={{ height: screenHeight * 100 }}
      >
        <SafeAreaView style={{ flex: 1 }}>
          <View style={{ flex: 1 }}>
            <ScrollView scrollEventThrottle={16}>
              <View style={{ marginTop: 20 }}>
                <Text
                  style={{
                    fontSize: 30,
                    fontWeight: "700",
                    color: "white",
                    alignSelf: "center",
                  }}
                >
                  Coding
                </Text>
                <View
                  // horizontal={true}
                  style={{
                    flexWrap: "wrap",
                    justifyContent: "space-between",
                    flexDirection: "row",
                    marginTop: 10,
                  }}
                >
                  {this.display()}
                </View>
              </View>
            </ScrollView>
          </View>
        </SafeAreaView>
      </LinearGradient>
    );
  }
}
