import React, { Component, useState } from "react";
import {
  ScrollView,
  LinearGradient,
  TextInput,
  StyleSheet,
  Text,
  TouchableOpacity,
  TouchableHighlight,
  View,
  ClippingRectangle,
} from "react-native";
import { Dimensions } from "react-native";
var screenWidth = Math.round(Dimensions.get("window").width) / 100;
var screenHeight = Math.round(Dimensions.get("window").height) / 100;
export default class YourApp extends Component {
  render() {
    return (
      <View style={{ backgroundColor: "#F1EEFc" }}>
        <LinearGradient
          // colors={["#fc00ff", "#00dbde"]}
          colors={["#09C6F9", "#045DE9"]}
          // colors={["#B621FE", "#1FD1F9"]}
          start={{ x: 1, y: 0 }}
          end={{ x: 0, y: 1 }}
          style={{ height: screenHeight * 100 }}
        >
          <View
            style={{
              flexDirection: "row",
              alignItems: "center",
              justifyContent: "center",
            }}
          >
            <Text
              style={{
                color: "#FFFFFF",
                fontSize: 32,
                fontWeight: "bold",
                marginTop: 40,
                marginBottom: 8,
                textAlign: "center",
              }}
            >
              Sign Up To Get Started
            </Text>
          </View>
          <View
            style={{
              flexDirection: this.state.what,
            }}
          >
            <Field
              name="name"
              component={myFields}
              label="Name "
              validate={[required]}
            />
            <Field
              name="userName"
              component={myFields}
              label="User_Name "
              validate={[required]}
            />
            <Field
              name="email"
              component={myFields}
              label="Email "
              validate={[required]}
            />
            <Field
              name="password"
              component={myFields}
              label="Password "
              validate={[required]}
            />
            <Field
              name="Repeat-Password"
              component={myFields}
              label="Repeat-Password "
              validate={[required]}
            />
            <View
              style={{
                flexDirection: "row",
                alignSelf: "center",
                marginVertical: 27,
                justifyContent: "space-between",
              }}
            >
              <Text
                style={{
                  fontSize: 24,
                  color: "white",
                  marginRight: screenWidth * 2,
                  textAlignVertical: "center",
                  alignSelf: "center",
                }}
              >
                Gender:
              </Text>
              <RadioForm
                buttonColor={"white"}
                buttonInnerColor={"white"}
                buttonOuterColor={"white"}
                style={styles.button}
                buttonSize={12}
                formHorizontal={true}
                labelHorizontal={true}
                radio_props={radio_props}
                labelStyle={{
                  fontSize: 20,
                  color: "white",
                  justifyContent: "space-around",
                }}
                initial={-1}
                onPress={(value) => {
                  this.setState({ gender: value });
                }}
              ></RadioForm>
            </View>
            <Button
              style={styles.customButton}
              onPress={this.props.handleSubmit(this.submit)}
              text="SignUp"
              textStyle={styles.textStyle}
            ></Button>
          </View>
        </LinearGradient>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    alignSelf: "center",
    marginTop: screenHeight * 7,
    width: screenWidth * 40,
    height: screenHeight,
  },
  modalView: {
    alignSelf: "center",
    backgroundColor: "white",
    borderRadius: 20,
    borderWidth: 0.9,
    padding: screenHeight * 4,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.95,
    shadowRadius: 10,
    elevation: 8,
  },
  pay: {
    backgroundColor: "#fc0f84",
    borderRadius: 20,
    padding: 10,
    width: screenWidth * 60,
    height: screenHeight * 7,
    alignSelf: "center",
    marginTop: screenHeight * 2,
  },
  applied: {
    marginTop: screenHeight * 2,
    fontSize: 16,
    color: "green",
    marginLeft: screenWidth * 2,
  },
  openButton: {
    backgroundColor: "#F194FF",
    borderRadius: 20,
    padding: 10,
    width: screenWidth * 70,
    height: screenHeight * 6,
    alignSelf: "center",
    marginTop: screenHeight * 5,
  },

  coupon: {
    backgroundColor: "#F194FF",
    borderRadius: 5,
    width: screenWidth * 25,
    height: screenHeight * 4,
    marginTop: screenHeight * 2,
  },
  textStyle: {
    color: "white",
    fontSize: 20,
    fontWeight: "bold",
    textAlignVertical: "center",
    textAlign: "center",
  },
  bookingDetails: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 28,
    marginBottom: screenHeight * 2,
  },
  textCoupon: {
    color: "white",
    fontSize: 15,
    fontWeight: "bold",
    textAlign: "center",
    textAlignVertical: "center",
  },
  textInput: {
    borderWidth: 1.5,
    borderRadius: 10,
    paddingLeft: 10,
    fontSize: 17,
    marginTop: screenHeight * 2,
  },
  modalText: {
    marginTop: screenHeight * 2,
    textAlignVertical: "center",
    fontSize: 18,
  },

  totalText: {
    marginTop: screenHeight * 2,
    marginTop: screenHeight * 2,
    fontSize: 25,
    fontWeight: "bold",
  },
  right: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 2,
    fontSize: 18,
  },

  lessonPrice: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 5,
    fontSize: 18,
  },

  lessonPrice1: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 3,
    fontSize: 18,
  },
  total: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 15,
    fontSize: 20,
  },
  lessonSummary: {
    fontWeight: "bold",
    fontSize: 18,
    marginTop: screenHeight * -0.5,
    marginBottom: screenHeight,
  },
  price: {
    marginTop: screenHeight * 3,
    fontSize: 18,
    fontWeight: "bold",
  },
});
