import React, { Component } from "react";
import { Text, View, TouchableOpacity, Picker, TextInput } from "react-native";
var disp = [];
export default class AddSkill extends Component {
  state = {
    count: 0,
  };
  renderComponents = () => {
    if (disp.length !== 0) disp = [];
    for (let i = 0; i < this.state.count; i++) {
      console.log(this.state.count, i);
      disp.push(i);
    }
  };
  renderify = () => {
    return disp.map((element) => (
      <View>
        <Picker
          style={{
            height: 50,
            width: this.state.width,
            marginVertical: 20,
            alignSelf: "center",
            color: "black",
            borderBottomWidth: 1.5,
            borderColor: "#D4E6F1",
            borderBottomColor: "black",
            backgroundColor: "#D4E6F1",
            paddingLeft: 220,
          }}
          selectedValue={this.state.state}
          onValueChange={this.handleValueChange}
          mode="dialog"
        >
          <Picker.Item label="Enter State" value="" />
          <Picker.Item label="Delhi" value="Delhi" />
          <Picker.Item label="Chhattisgarh" value="Chhattisgarh" />
          <Picker.Item label="Goa" value="Goa" />
          <Picker.Item label="Gujarat" value="Gujarat" />
          <Picker.Item label="Haryana" value="Haryana" />
          <Picker.Item label="Jharkhand" value="Jharkhand" />
          <Picker.Item label="Karnataka" value="Karnataka" />
          <Picker.Item label="Kerala" value="Kerala" />
          <Picker.Item label="Madhya Pradesh" value="Madhya Pradesh" />
          <Picker.Item label="Maharashtra" value="Maharashtra" />
          <Picker.Item label="Punjab" value="Punjab" />
          <Picker.Item label="Rajasthan" value="Rajasthan" />
          <Picker.Item label="Tamil Nadu" value="Tamil Nadu" />
          <Picker.Item label="Uttar Pradesh" value="Uttar Pradesh" />
        </Picker>

        <Text style={{ marginTop: 80 }}>Hey There{element}</Text>
        <TouchableOpacity
          key={10}
          onPress={(item) => {
            disp.splice(disp.indexOf(element), 1);
            this.setState({ count: this.state.count - 1 });
          }}
        >
          <Text>Press Me {element}</Text>
        </TouchableOpacity>
      </View>
    ));
  };
  render() {
    return (
      <View>
        <Text>{this.state.count}</Text>
        <TouchableOpacity
          onPress={() => this.setState({ count: this.state.count + 1 })}
        >
          <Text>Add More AddSkill</Text>
        </TouchableOpacity>
        {this.renderComponents()}
        {this.renderify()}
      </View>
    );
  }
}
