import React, { Component } from "react";
import { Text, View, TouchableOpacity, Picker, TextInput } from "react-native";
var disp = [];
export default class AddSkill extends Component {
  state = {
    count: 0,
  };
  renderComponents = () => {
    if (disp.length !== 0) disp = [];
    for (let i = 0; i < this.state.count; i++) {
      console.log(this.state.count, i);
      disp.push(
        <View key={i}>
          <Text style={{ marginTop: 80 }}>Hey There{i}</Text>
          <TouchableOpacity
            key={10}
            onPress={(item) => {
              disp.splice(i, 1);
              this.setState({ count: this.state.count - 1 });
            }}
          >
            <Text>Press Me {i}</Text>
          </TouchableOpacity>
        </View>
      );
    }
  };
  renderify = () => {
    return disp.map((elemet) => (
      <View>
        {" "}
        <Text style={{ marginTop: 80 }}>Hey There{i}</Text>
        <TouchableOpacity
          key={10}
          onPress={(item) => {
            disp.splice(i, 1);
            this.setState({ count: this.state.count - 1 });
          }}
        >
          <Text>Press Me {i}</Text>
        </TouchableOpacity>
      </View>
    ));
  };
  render() {
    return (
      <View>
        <Text>{this.state.count}</Text>
        <TouchableOpacity
          onPress={() => this.setState({ count: this.state.count + 1 })}
        >
          <Text>Add More AddSkill</Text>
        </TouchableOpacity>
        {this.renderComponents()}
        {this.renderify()}
      </View>
    );
  }
}
