class GalleryButton extends React.Component {
  render() {
    const { onPress, source } = this.props;
    const size = 36;
    const enabled = !DISABLE_BOTTOM_DRAWER;
    return (
      <TouchableOpacity
        style={{ width: size, height: size }}
        pointerEvents={enabled ? "auto" : "none"}
        onPress={onPress}
      >
        <Image
          source={source}
          style={{
            opacity: enabled ? 1 : 0.5,
            flex: 1,
            resizeMode: "contain",
            borderRadius: 4,
            borderWidth: StyleSheet.hairlineWidth,
            borderColor: "white",
          }}
        />
      </TouchableOpacity>
    );
  }
}

class RotatingIcon extends React.Component {
  state = { index: 0 };

  componentDidMount() {
    if (this.viewPager)
      this.viewPager.scrollToIndex({
        index: this.props.index,
        animated: false,
      });
  }
  componentDidUpdate(prevProps) {
    if (this.props.index !== prevProps.index) {
      if (this.viewPager) {
        this.viewPager.scrollToIndex({
          index: this.props.index,
          duration: 1000,
        });
      }
    }
  }

  renderItem = ({ item, index }) => {
    const { itemWidth } = this.props;
    const maxRotation = 80;
    const inputMargin = itemWidth * 0.333333333;
    if (!item) {
      return <View style={{ width: itemWidth }} />;
    }
    const animatedStyle = {
      width: itemWidth,
      height: itemWidth,
      padding: 4,
      transform: [
        {
          rotateY: this.animatedValue.interpolate({
            inputRange: [
              (index - 1) * itemWidth + inputMargin,
              index * itemWidth,
              (index + 1) * itemWidth - inputMargin,
            ],
            outputRange: [`-${maxRotation}deg`, "0deg", `${maxRotation}deg`],
          }),
        },
      ],
    };
    return (
      <Animated.View style={animatedStyle}>
        <Image style={{ flex: 1, resizeMode: "contain" }} source={item} />
      </Animated.View>
    );
  };
  animatedValue = new Animated.Value(0);
  render() {
    const { itemWidth, data } = this.props;

    return (
      <ViewPager
        pagingEnabled
        useNativeDriver={Platform.OS !== "web"}
        scroll={this.animatedValue}
        ref={(ref) => (this.viewPager = ref)}
        data={data}
        renderItem={this.renderItem}
        style={{
          minHeight: itemWidth,
          maxHeight: itemWidth,
          maxWidth: itemWidth,
          minWidth: itemWidth,
        }}
        size={itemWidth}
        horizontal
      />
    );
  }
}
