  //     user = firebase.auth().currentUser;
  //     BackHandler.addEventListener("hardwareBackPress", this.handleBackButton);
  //     await this.loadFonts();
  //     this.setState({ fontsLoaded: true });
  //   }
  loadFonts() {
    return Font.loadAsync({
      "Poppins-ExtraLight": require("./assets/fonts/Poppins-ExtraLight.ttf"),
      "Poppins-Medium": require("./assets/fonts/Poppins-Medium.ttf"),
      "Poppins-Bold": require("./assets/fonts/Poppins-Bold.ttf"),
    });
  }
  componentWillUnmount() {
    BackHandler.removeEventListener("hardwareBackPress", this.handleBackButton);
  }

  handleBackButton = () => {
    this.props.currentIndex({ index: 3 });
    this.props.navigation.navigate("Entry");
    return true;
  };
  handleValueChange = (value) => {
    this.setState({ state: value });
  };

  updatecity = async (value) => {
    await this.setState({ city: value });
  };

  pickImage = async () => {
    console.log("slfnsdfnm");
    let pickerResult = await ImagePicker.launchImageLibraryAsync({
      allowsEditing: true,
      aspect: [4, 3],
    });
    console.log(pickerResult);
    await this.setState({ loading: true });
    await this._handleImagePicked(pickerResult);
  };
  takePhoto = async () => {
    let pickerResult = await ImagePicker.launchCameraAsync({
      allowsEditing: true,
      aspect: [4, 3],
    });
    await this.setState({ loading: true });
    await this._handleImagePicked(pickerResult);
  };

  _handleImagePicked = async (pickerResult) => {
    try {
      this.setState({ uploading: true });
      if (!pickerResult.cancelled) {
        uploadUrl = await uploadImageAsync(pickerResult.uri);
        await this.setState({ image: uploadUrl });
        await firebase
          .database()
          .ref("user/" + this.props.user.id)
          .update({
            photo: this.state.image,
          });
        await this.setState({ loading: false });
      }
    } catch (e) {
      console.log(e);
      alert("Upload failed, sorry :(");
    } finally {
      this.setState({ uploading: false });
    }
  };
  change = async () => {
    if (!this.state.edit) {
      this.setState({ edit: true });
    } else {
      await firebase
        .database()
        .ref("user/" + 1)
        .update({
          name: "Hetan Thakkar",
        });
      newPassword = "523212";
      user
        .updatePassword(newPassword)
        .then(function() {
          user
            .updateEmail("user@gmail.com")
            .then(async () => {
