import React, { Component } from "react";
import { Text, View, TouchableOpacity, Picker, TextInput } from "react-native";
var disp = [];
export default class AddSkill extends Component {
  state = {
    count: 0,
    disp: [],
  };
  renderComponents = () => {
    if (this.state.disp.length !== 0) this.setState({ disp: [] });
    for (let i = 0; i < this.state.count; i++) {
      let a = [];
      //   console.log(this.state.count, i);
      a.push(
        <View key={i}>
          <Text style={{ marginTop: 80 }}>Hey There{i}</Text>
          <TouchableOpacity
            key={10}
            onPress={(item) => {
              //   console.log(disp);
              disp.splice(i, 1);
              //   console.log(disp);
            }}
          >
            <Text>Press Me {i}</Text>
          </TouchableOpacity>
        </View>
      );
    }
    this.setState({ disp: a });
    console.log(disp);
  };
  renderify = () => {
    return disp.map((elemet) => elemet);
  };
  render() {
    return (
      <View>
        <Text>{this.state.count}</Text>
        <TouchableOpacity
          onPress={() => {
            this.setState({ count: this.state.count + 1 });
            this.renderify;
          }}
        >
          <Text>Add More AddSkill</Text>
        </TouchableOpacity>
        {this.renderComponents()}
        {this.renderify()}
      </View>
    );
  }
}
