  //         <GiftedChat
  //           messages={messages}
  //           onSend={(messages) => onSend(messages)}
  //           user={{
  //             _id: 1,
  //           }}
  //         />
  //       </View>
  //     </View>
  //   );
  // }
  if (Constants.platform.web.ua.includes("Windows")) {
    return (
      <View>
        <Text>sldmf</Text>
      </View>
    );
  }
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight || 0,
  },
  item: {
    backgroundColor: "#f9c2ff",
    padding: 20,
    marginVertical: 8,
    marginHorizontal: 16,
  },
  title: {
    fontSize: 32,
  },
});
