import React, { Component } from "react";
import RadioForm, {
  RadioButton,
  RadioButtonInput,
  RadioButtonLabel,
} from "react-native-simple-radio-button";
import { Button } from "./button/index";
import { CheckBox } from "react-native-elements";
import {
  View,
  Text,
  Picker,
  StyleSheet,
  Image,
  TouchableOpacity,
  TextInput,
  Dimensions,
  ActivityIndicator,
  BackHandler,
} from "react-native";
import styles from "./styles1/styles1";
import Constants from "expo-constants";
import * as ImagePicker from "expo-image-picker";
import { LinearGradient } from "expo-linear-gradient";
import * as Font from "expo-font";
import Icon from "./icons";
var disp = [];
import firebase from "firebase";
var screenHeight = Math.round(Dimensions.get("window").height) / 100;
var screenWidth = Math.round(Dimensions.get("window").width) / 100;
var firebaseConfig = {
  apiKey: "AIzaSyAfGN94rWhA55dceve-ab5R5nEL6o4xXeg",
  authDomain: "new1-930be.firebaseapp.com",
  databaseURL: "https://new1-930be.firebaseio.com",
  projectId: "new1-930be",
  storageBucket: "new1-930be.appspot.com",
  messagingSenderId: "332990256430",
  appId: "1:332990256430:web:640a6413492c34bf2a96bf",
  measurementId: "G-SBPS6449GM",
};
var radio_props = [
  { label: "Male    ", value: "Male" },
  { label: "Female", value: "Female" },
];
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}
var newPassword, userName, email, user, password, city, state, uploadUrl, photo;
export default class AccountEdit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fontsLoaded: false,
      edit: false,
      state: "Enter State",
      city: "",
      name: "Hetan Thakkar",
      email: "hetanthakkar1@gmail.com",
      password: "523212",
      userName: "Hetan",
      state1: "Gujarat",
      city1: "Bhavnagar",
      loading: false,
      visible: false,
      multiply: 7,
      width1: screenWidth * 27,
      orientation: "row",
      width3: screenWidth * -71,
      width: screenWidth * 60,
      width2: screenWidth * 13,
      width4: screenWidth * 15,
      bio:
        "Hetan started her career in software development as a contractor helping small businesses develop their apps. She is currently a software developer at Rangle.io in Toronto focussed on building web applications for clients using modern JavaScript",
      image:
        "https://drive.google.com/file/d/17ueAQQ5uVWX7lZ8YQ5pguzTmxOam-YXb/view?usp=sharing",
    };
  }
  warn = () => {
    if (disp.length == 0) {
      return (
        <Text
          style={{
            color: "red",
            fontSize: 17,
            textAlignVertical: "true",
            marginLeft: screenWidth * 2,
            marginTop: screenHeight * 2,
          }}
        >
          You Must add atleast 1 Skill !
        </Text>
      );
    }
  };

  render() {
    if (true) {
      return (
        <View style={{ backgroundColor: "#F1EEFc", height: "100%" }}>
          <LinearGradient
            colors={["#fc0f84", "#020cab"]}
            start={{ x: 1, y: 0 }}
            end={{ x: 0, y: 1 }}
            style={{ height: screenHeight * 100 }}
          >
            <View style={{ height: "100%" }}>
              <View
                style={{
                  width: "70%",
                  backgroundColor: "white",
                  alignSelf: "center",
                  borderRadius: 22,
                }}
              >
                <Text
                  style={{
                    alignSelf: "center",
                    fontSize: 32,
                    fontWeight: "bold",
                    marginVertical: screenHeight * 3,
                  }}
                >
                  Tell Us About Yourself
                </Text>
                <Text
                  style={{
                    marginHorizontal: screenWidth * 1,
                    fontSize: 18,
                    fontWeight: "500",
                    marginVertical: screenHeight * 2,
                  }}
                >
                  1. Enter Your Preferred Teaching Time
                </Text>
                <CheckBox
                  style={{
                    marginHorizontal: screenWidth * 2,
                    marginTop: screenHeight * -1,
                  }}
                  title="10:00 AM - 12:00 PM"
                  checked={this.state.checked}
                  onPress={() =>
                    this.setState({ checked: !this.state.checked })
                  }
                />
                <CheckBox
                  title="2:00 PM - 6:00 PM"
                  checked={this.state.checked}
                  onPress={() =>
                    this.setState({ checked: !this.state.checked })
                  }
                />
                <CheckBox
                  title="6:00 PM - 9:00 PM"
                  checked={this.state.checked}
                  onPress={() =>
                    this.setState({ checked: !this.state.checked })
                  }
                />
                <CheckBox
                  title="Mid-Night / Early-Morning"
                  checked={this.state.checked}
                  onPress={() =>
                    this.setState({ checked: !this.state.checked })
                  }
                />
                <Text
                  style={{
                    marginHorizontal: screenWidth * 1,
                    fontSize: 18,
                    fontWeight: "500",
                    marginVertical: screenHeight * 2,
                  }}
                >
                  2. Enter Your Preferred Teaching Destination
                </Text>
                <CheckBox
                  style={{
                    marginHorizontal: screenWidth * 2,
                    marginTop: screenHeight * -1,
                  }}
                  title="My House"
                  checked={this.state.checked}
                  onPress={() =>
                    this.setState({ checked: !this.state.checked })
                  }
                />
                <CheckBox
                  title="Student's House"
                  checked={this.state.checked}
                  onPress={() =>
                    this.setState({ checked: !this.state.checked })
                  }
                />
                <CheckBox
                  title="Outside Place"
                  checked={this.state.checked}
                  onPress={() =>
                    this.setState({ checked: !this.state.checked })
                  }
                />
                <View
                  style={{ flexDirection: "row", backgroundColor: "white" }}
                >
                  <Text
                    style={{
                      marginHorizontal: screenWidth * 1,
                      fontSize: 18,
                      marginTop: screenHeight * 2,
                    }}
                  >
                    3. Add Skills
                  </Text>

                  <TouchableOpacity
                    style={{ marginTop: screenHeight * 2 }}
                    onPress={() => {
                      if (this.state.count <= 3) {
                        this.setState({ count: this.state.count + 1 });
                        disp.push(this.state.count);
                      } else {
                        console.log("Fuck off");
                      }
                    }}
                  >
                    <Icon
                      family="MaterialIcons"
                      name="add"
                      size={25}
                      color="black"
                    />
                  </TouchableOpacity>
                </View>
                {this.warn()}
                {this.renderify()}

                <TouchableOpacity
                  onPress={() => this.props.navigation.navigate("HomePage")}
                  style={{
                    marginTop: screenHeight * 4,
                    alignSelf: "center",
                    backgroundColor: "#0984e3",
                    borderRadius: 12,
                    width: screenWidth * 20,
                    height: screenHeight * 6,
                    marginBottom: screenHeight * 2,
                  }}
                >
                  <Text
                    style={{
                      color: "#EAF0F1",
                      textAlign: "center",
                      fontWeight: "bold",
                      fontSize: 25,
                      textAlignVertical: "center",
                    }}
                  >
                    Submit
                  </Text>
                </TouchableOpacity>
              </View>
            </View>
          </LinearGradient>
        </View>
      );
    } else {
      return <View />;
    }
  }
}
async function uploadImageAsync(uri) {
  const blob = await new Promise((resolve, reject) => {
    const xhr = new XMLHttpRequest();
    xhr.onload = function() {
      resolve(xhr.response);
    };
    xhr.onerror = function(e) {
      console.log(e);
      reject(new TypeError("Network request failed"));
    };
    xhr.responseType = "blob";
    xhr.open("GET", uri, true);
    xhr.send(null);
  });

  const ref = firebase
    .storage()
    .ref()
    .child("ksjdnf");
  const snapshot = await ref.put(blob);
  blob.close();
  return await snapshot.ref.getDownloadURL();
}
