import React, { Component } from "react";
import {
  Animated,
  Text,
  StyleSheet,
  LinearGradient,
  View,
  ScrollView,
  FlatList,
  Image,
  Dimensions,
  Platform,
  TouchableOpacity,
} from "react-native";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import * as theme from "./theme1";
var screenHeight = Math.round(Dimensions.get("window").height) / 100;

const { width } = Dimensions.get("window");
const styles = StyleSheet.create({
  flex: {
    flex: 0.5,
  },
  column: {
    flexDirection: "column",
  },
  row: {
    flexDirection: "row",
  },
  header: {
    backgroundColor: theme.colors.white,
    paddingHorizontal: theme.sizes.padding,
    paddingTop: theme.sizes.padding * 1.33,
    paddingBottom: theme.sizes.padding * 0.66,
    justifyContent: "space-between",
    alignItems: "center",
  },
  articles: {},
  destinations: {
    flex: 1,
    justifyContent: "space-between",
    paddingBottom: 30,
  },
  destination: {
    width: width - theme.sizes.padding * 2,
    height: width * 0.6,
    marginHorizontal: theme.sizes.margin,
    paddingHorizontal: theme.sizes.padding,
    paddingVertical: theme.sizes.padding * 0.66,
    borderRadius: theme.sizes.radius,
  },
  destinationInfo: {
    position: "absolute",
    borderRadius: theme.sizes.radius,
    paddingHorizontal: theme.sizes.padding,
    paddingVertical: theme.sizes.padding / 2,
    bottom: 20,
    left: (width - theme.sizes.padding * 4) / (Platform.OS === "ios" ? 3.2 : 3),
    backgroundColor: theme.colors.white,
    width: width - theme.sizes.padding * 4,
  },
  recommendedHeader: {
    justifyContent: "space-between",
    alignItems: "flex-end",
    paddingHorizontal: theme.sizes.padding,
  },
  recommendation: {
    width: (width - theme.sizes.padding * 2) / 7,
    height: (width - theme.sizes.padding * 2) / 100,
    marginHorizontal: 8,
    backgroundColor: theme.colors.white,
    borderRadius: theme.sizes.radius,
    marginVertical: theme.sizes.margin * 0.5,
  },
  recommendationImage: {
    width: (width - theme.sizes.padding * 2) / 8,
    height: (width - theme.sizes.padding * 2) / 8,
  },
  avatar: {
    width: theme.sizes.padding,
    height: theme.sizes.padding,
    borderRadius: theme.sizes.padding / 2,
  },
  rating: {
    fontSize: theme.sizes.font * 2,
    color: theme.colors.white,
    fontWeight: "bold",
  },
  shadow: {
    shadowColor: theme.colors.black,
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.05,
    shadowRadius: 10,
    elevation: 5,
  },
});

class Articles extends Component {
  scrollX = new Animated.Value(0);

  static navigationOptions = {
    header: (
      <View style={[styles.flex, styles.row, styles.header]}>
        <View>
          <Text style={{ color: theme.colors.caption }}>Search for place</Text>
          <Text style={{ fontSize: theme.sizes.font * 2 }}>Destination</Text>
        </View>
        <View>
          <Image
            style={styles.avatar}
            source={{ uri: "https://randomuser.me/api/portraits/women/32.jpg" }}
          />
        </View>
      </View>
    ),
  };

  renderRatings(rating) {
    const stars = new Array(5).fill(0);
    return stars.map((_, index) => {
      const activeStar = Math.floor(rating) >= index + 1;
      return (
        <FontAwesome
          name="star"
          key={`star-${index}`}
          size={theme.sizes.font}
          color={theme.colors[activeStar ? "active" : "gray"]}
        />
      );
    });
  }

  renderRecommended = (title) => {
    return (
      <View style={[styles.flex, styles.column, styles.recommended]}>
        <View style={[styles.row, styles.recommendedHeader]}>
          <Text style={{ fontSize: theme.sizes.font * 1.4 }}>{title}</Text>
          <TouchableOpacity activeOpacity={0.5}>
            <Text style={{ color: theme.colors.caption }}>More</Text>
          </TouchableOpacity>
        </View>
        <View style={[styles.column]}>
          <FlatList
            horizontal
            pagingEnabled
            scrollEnabled
            showsHorizontalScrollIndicator={false}
            scrollEventThrottle={16}
            snapToAlignment="center"
            style={[styles.shadow, { overflow: "visible" }]}
            data={this.props.destinations}
            keyExtractor={(item, index) => `${item.id}`}
            renderItem={({ item, index }) =>
              this.renderRecommendation(item, index)
            }
          />
        </View>
      </View>
    );
  };

  renderRecommendation = (item, index) => {
    const { destinations } = this.props;
    const isLastItem = index === destinations.length - 1;
    return (
      <View
        style={[
          styles.flex,
          styles.column,
          styles.recommendation,
          styles.shadow,
          index === 0 ? { marginLeft: theme.sizes.margin } : null,
          isLastItem ? { marginRight: theme.sizes.margin / 2 } : null,
        ]}
      >
        <View style={[styles.flex]}>
          <Image
            style={[styles.recommendationImage]}
            source={{ uri: item.preview }}
          />
        </View>
        <View
          style={[
            styles.flex,
            styles.column,
            styles.shadow,
            {
              justifyContent: "space-evenly",
              padding: theme.sizes.padding / 2,
            },
          ]}
        >
          <Text
            style={{
              fontSize: theme.sizes.font * 1.25,
              fontWeight: "500",
              paddingBottom: theme.sizes.padding / 4.5,
            }}
          >
            {item.title}
          </Text>
          <Text style={{ color: theme.colors.caption }}>{item.location}</Text>
          <View
            style={[
              styles.row,
              {
                alignItems: "center",
                justifyContent: "space-between",
                marginTop: theme.sizes.margin,
              },
            ]}
          >
            {this.renderRatings(item.rating)}
            <Text style={{ color: theme.colors.active }}>{item.rating}</Text>
          </View>
        </View>
      </View>
    );
  };

  render() {
    return (
      <LinearGradient
        colors={["#09C6F9", "#045DE9"]}
        start={{ x: 1, y: 0 }}
        end={{ x: 0, y: 1 }}
        style={{ height: screenHeight * 100 }}
      >
        <View
          style={[
            styles.flex,
            styles.column,
            styles.recommendation,
            styles.shadow,
          ]}
        >
          <View style={[styles.flex, styles.recommendationHeader]}>
            <Image
              style={[styles.recommendationImage]}
              source={{
                uri:
                  "https://images.unsplash.com/photo-1507501336603-6e31db2be093?auto=format&fit=crop&w=800&q=80",
              }}
            />
          </View>
          <View
            style={[
              styles.flex,
              styles.column,
              styles.shadow,
              {
                justifyContent: "space-evenly",
                padding: theme.sizes.padding / 2,
              },
            ]}
          >
            <Text
              style={{
                marginBottom: -25,
                marginTop: -20,
                fontSize: theme.sizes.font * 1.25,
                fontWeight: "500",
              }}
            >
              odihfowhief
            </Text>
            <Text style={{ color: theme.colors.caption }}>jdsnksdn</Text>
            <View
              style={[
                styles.row,
                {
                  alignItems: "center",
                  justifyContent: "space-between",
                },
              ]}
            >
              {this.renderRatings(4.3)}
              <Text style={{ color: theme.colors.active }}>4.3</Text>
            </View>
          </View>
        </View>
      </LinearGradient>
    );
  }
}

Articles.defaultProps = {
  destinations: mocks,
};

export default Articles;
