        "Bangalore",
        "Mysore",
        "Bijapur",
      ].map((city) => <Picker.Item label={city} value={city} />);
    if (this.state.state == "Rajasthan")
      return [
        "Enter City",
        "Kota",
        "Udaipur",
        "Jaipur",
        "Jodhpur",
        "Sikar",
        "Ajmer",
      ].map((city) => <Picker.Item label={city} value={city} />);
    if (this.state.state == "Tamil Nadu")
      return [
        "Enter City",
        "Coimbatore",
        "Salem",
        "Madurai",
        "Tiruchirapalli",
      ].map((city) => <Picker.Item label={city} value={city} />);
    if (this.state.state == "Uttar Pradesh")
      return [
        "Enter City",
        "Kanpur",
        "Lucknow",
        "Ghaziabad",
        "Agra",
        "Varanasi",
        "Prayagraj",
      ].map((city) => <Picker.Item label={city} value={city} />);
    if (this.state.state == "Delhi")
      return ["Enter City", "Delhi NCR"].map((city) => (
        <Picker.Item label={city} value={city} />
      ));
    if (this.state.state == "")
      return <Picker.Item label="Enter City" value="" />;
  };
  renderPicker = () => {
    if (
      !this.state.skill == "student" ||
      this.state.skill === "Web Development" ||
      this.state.skill === "Computer Language" ||
      this.state.skill === "New Language" ||
      this.state.skill === "Musical Instrument"
    ) {
      return (
        <Picker
          style={{
            height: 50,
            width: "40%",
            marginVertical: 20,
            alignSelf: "center",
            color: "black",
            borderRadius: 12,
            borderBottomWidth: 2,
            borderColor: "#0984e3",
            backgroundColor: "white",
            paddingLeft: 160,
          }}
          onValueChange={(itemValue) => this.updateSpecifics(itemValue)}
          selectedValue={this.state.specifics}
          textStyle={{ width: screenWidth * 100, color: "white" }}
          mode="dialog"
        >
          {this.getCities()}
        </Picker>
      );
    }
  };
  getCities = () => {
    if (this.state.skill === "")
      return ["Select Specific Skill"].map((specifics) => (
        <Picker.Item label={specifics} value={specifics} />
      ));

    if (this.state.skill === "Computer Language")
      return [
        "Enter Language",
        "C",
        "C++",
        "Java",
        "JavaScript",
        "Python",
        "Other",
      ].map((specifics) => <Picker.Item label={specifics} value={specifics} />);
    if (this.state.skill === "New Language")
      return [
        "Enter Language",
        "English",
        "Spanish",
        "French",
        "Japanese",
        "Italian",
        "German",
        "Other",
      ].map((specifics) => <Picker.Item label={specifics} value={specifics} />);

    if (this.state.skill === "Musical Instrument")
      return [
        "Enter Instrument",
        "Piano/Keyboard",
        "Drum",
        "FLute",
        "Guitar",
        "Sitar",
        "Other",
      ].map((specifics) => <Picker.Item label={specifics} value={specifics} />);

    if (this.state.skill === "Web Development")
      return [
        "Enter Technology",
        "Djamgo",
        "Flask",
        "Php",
        "Other",
      ].map((specifics) => <Picker.Item label={specifics} value={specifics} />);

    if (this.state.skill === "")
      return [" "].map((specifics) => (
        <Picker.Item label={specifics} value={specifics} />
      ));
    if (this.state.skill === "Other")
      return ["Enter Your SKill"].map((specifics) => (
        <Picker.Item label={specifics} value={specifics} />
      ));
  };

  updateSkill = (skill) => {
    this.setState({ skill });
    this.setState({ specifics: "" });
    console.log(this.state.skill);
  };
  handleValueChange = (value) => {
    this.setState({ state: value });
  };
  render() {
    if (true) {
      return (
        <View style={{ backgroundColor: "#F1EEFc", height: "100%" }}>
          <LinearGradient
            colors={["#fc0f84", "#020cab"]}
            start={{ x: 1, y: 0 }}
            end={{ x: 0, y: 1 }}
            style={{ flex: 1 }}
          >
            <View style={{ height: "100%" }}>
              <View
                style={{
                  width: "70%",
                  backgroundColor: "white",
                  alignSelf: "center",
                  borderRadius: 22,
                }}
              >
                <Text
                  style={{
                    alignSelf: "center",
                    fontSize: 32,
                    fontWeight: "bold",
                    marginVertical: screenHeight * 3,
                  }}
                >
                  What Do You Want To Learn
                </Text>
                <Picker
                  style={{
                    height: 50,
                    width: "40%",
                    marginVertical: 20,
                    alignSelf: "center",
                    color: "black",
                    borderRadius: 12,
                    borderBottomWidth: 2,
                    borderColor: "#0984e3",
                    backgroundColor: "white",
                    paddingLeft: 160,
                  }}
                  selectedValue={this.state.state}
                  onValueChange={this.handleValueChange}
                  mode="dialog"
                  textStyle={styles.pickerText}
                >
                  <Picker.Item label="Enter State" value="" />
                  <Picker.Item label="Delhi" value="Delhi" />
                  <Picker.Item label="Chhattisgarh" value="Chhattisgarh" />
                  <Picker.Item label="Goa" value="Goa" />
                  <Picker.Item label="Gujarat" value="Gujarat" />
                  <Picker.Item label="Haryana" value="Haryana" />
                  <Picker.Item label="Jharkhand" value="Jharkhand" />
                  <Picker.Item label="Karnataka" value="Karnataka" />
                  <Picker.Item label="Kerala" value="Kerala" />
                  <Picker.Item label="Madhya Pradesh" value="Madhya Pradesh" />
                  <Picker.Item label="Maharashtra" value="Maharashtra" />
                  <Picker.Item label="Punjab" value="Punjab" />
                  <Picker.Item label="Rajasthan" value="Rajasthan" />
                  <Picker.Item label="Tamil Nadu" value="Tamil Nadu" />
                  <Picker.Item label="Uttar Pradesh" value="Uttar Pradesh" />
                </Picker>
                <Picker
                  style={{
                    height: 50,
                    width: "40%",
                    marginVertical: 20,
                    alignSelf: "center",
                    color: "black",
                    borderRadius: 12,
                    borderBottomWidth: 2,
                    borderColor: "#0984e3",
                    backgroundColor: "white",
                    paddingLeft: 160,
                  }}
                  onValueChange={(itemValue) => this.updatecity(itemValue)}
                  selectedValue={this.state.city}
                  mode="dialog"
                  textStyle={styles.pickerText}
                >
                  {this.getcityes()}
                </Picker>
                <Picker
                  style={{
                    height: 50,
                    width: "40%",
                    marginVertical: 20,
                    alignSelf: "center",
                    color: "black",
                    borderRadius: 12,
                    borderBottomWidth: 2,
                    borderColor: "#0984e3",
                    backgroundColor: "white",
                    paddingLeft: 160,
                  }}
                  selectedValue={this.state.skill}
                  onValueChange={this.updateSkill}
                  textStyle={{ width: screenWidth * 100, color: "white" }}
                  mode="dialog"
                >
                  <Picker.Item label="Select Skill: " value="" />
                  <Picker.Item label="No Skill" value="student" />
                  <Picker.Item label="Photography" value="Photography" />
                  <Picker.Item label="Videography" value="Videography" />
                  <Picker.Item label="Photoshop" value="Photoshop" />
                  <Picker.Item label="Animation" value="Animation" />
                  <Picker.Item
                    label="Digital Marketing"
                    value="Digital Marketing"
                  />
                  <Picker.Item label="Buisness" value="Buisness" />
                  <Picker.Item
                    label="Musical Instrument"
                    value="Musical Instrument"
                  />
                  <Picker.Item label="Cooking" value="Cooking" />
                  <Picker.Item label="Singing" value="Singing" />
                  <Picker.Item label="Drawing" value="Drawing" />
                  <Picker.Item label="New Language" value="New Language" />
                  <Picker.Item
                    label="Computer Language"
                    value="Computer Language"
                  />
                  <Picker.Item label="Cyber Security" value="Cyber Security" />
                  <Picker.Item
                    label="Artificial Intelligence"
