import React, { Component, useState } from "react";
import {
  ScrollView,
  Alert,
  TextInput,
  StyleSheet,
  Text,
  LinearGradient,
  TouchableOpacity,
  TouchableHighlight,
  View,
  ClippingRectangle,
} from "react-native";
import { RegularText, BoldText, SemiBoldText } from "./components/StyledText";
import { RectButton } from "react-native-gesture-handler";
import { Colors, Layout, FontSizes } from "./constants";
import { Dimensions } from "react-native";
var screenWidth = Math.round(Dimensions.get("window").width) / 100;
var screenHeight = Math.round(Dimensions.get("window").height) / 100;
const App = () => {
  const [modalVisible, setModalVisible] = useState(false);
  return (
    <ScrollView style={styles.centeredView}>
      <LinearGradient
        // colors={["#fc00ff", "#00dbde"]}
        colors={["#09C6F9", "#045DE9"]}
        // colors={["#B621FE", "#1FD1F9"]}
        start={{ x: 1, y: 0 }}
        end={{ x: 0, y: 1 }}
        style={{ height: screenHeight * 100 }}
      >
        <Text style={styles.bookingDetails}>Booking Details</Text>
        <ScrollView style={styles.modalView}>
          <Text style={styles.lessonSummary}>Lesson Summary</Text>
          <View style={{ flexDirection: "row" }}>
            <Text style={styles.modalText}> Tutor Name: </Text>
            <Text style={styles.right}> Hetan Thakkar </Text>
          </View>
          <View style={{ flexDirection: "row" }}>
            <Text style={styles.modalText}>Lesson Length:</Text>
            <Text style={styles.right}>45 minutes </Text>
          </View>
          <View style={{ flexDirection: "row" }}>
            <Text style={styles.modalText}>Lesson Name: </Text>
            <Text style={styles.right}> React Native </Text>
          </View>
          <View
            style={{
              borderBottomColor: "grey",
              borderBottomWidth: 1,
              marginTop: screenHeight * 3,
            }}
          />
          <Text style={styles.price}>Price Summary </Text>
          <View style={{ flexDirection: "row" }}>
            <Text style={styles.modalText}>Lesson Price:</Text>
            <Text style={styles.lessonPrice1}>$300 </Text>
          </View>
          <Text style={styles.price}>Apply Coupon Code </Text>
          <TextInput style={styles.textInput}></TextInput>
          <View style={{ flexDirection: "row" }}>
            <RectButton
              style={styles.bigButton}
              // onPress={this._handlePressJobUrl}
              underlayColor="#fff"
            >
              <SemiBoldText style={styles.bigButtonText}>
                Apply Coupon
              </SemiBoldText>
            </RectButton>
          </View>
          <View style={{ flexDirection: "row" }}>
            <Text style={styles.modalText}>Deducted Price: </Text>
            <Text style={styles.right}>- $100 </Text>
          </View>
          <View
            style={{
              borderBottomColor: "grey",
              borderBottomWidth: 1,
              marginTop: screenHeight * 3,
            }}
          />

          <View style={{ flexDirection: "row" }}>
            <Text style={styles.totalText}>Total : </Text>
            <Text style={styles.total}>$ 200 </Text>
          </View>
        </ScrollView>
        <TouchableHighlight
          style={styles.pay}
          onPress={() => {
            setModalVisible(true);
          }}
        >
          <Text style={styles.textStyle}>Proceed to Pay</Text>
        </TouchableHighlight>
      </LinearGradient>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  bigButton: {
    backgroundColor: Colors.blue,
    paddingHorizontal: 15,
    height: 35,
    width: "40%",
    marginVertical: 10,
    alignSelf: "center",
    justifyContent: "center",
    borderRadius: 20,
    overflow: "hidden",
    marginHorizontal: 100,
  },

  bigButtonText: {
    fontSize: FontSizes.normalButton,
    fontWeight: "bold",
    color: "#fff",
    textAlign: "center",
  },
  centeredView: {
    flex: 1,
    alignSelf: "center",
    marginTop: screenHeight * 2,
    height: screenHeight,
  },
  modalView: {
    backgroundColor: "white",
    borderRadius: 20,
    alignSelf: "center",
    borderWidth: 0.9,
    padding: screenHeight * 2,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.95,
    shadowRadius: 10,
    elevation: 8,
  },
  pay: {
    backgroundColor: "#fc0f84",
    borderRadius: 20,
    padding: 10,
    width: screenWidth * 60,
    height: screenHeight * 7,
    alignSelf: "center",
    marginTop: screenHeight * 4,
  },
  applied: {
    marginTop: screenHeight * 2,
    fontSize: 16,
    color: "green",
    marginLeft: screenWidth,
  },
  openButton: {
    backgroundColor: "#F194FF",
    borderRadius: 20,
    padding: 10,
    width: screenWidth * 70,
    height: screenHeight * 6,
    alignSelf: "center",
    marginTop: screenHeight * 5,
  },

  coupon: {
    backgroundColor: "#F194FF",
    borderRadius: 5,
    width: screenWidth * 60,
    height: screenHeight * 4,
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 42,
  },
  textStyle: {
    color: "white",
    fontSize: 20,
    fontWeight: "bold",
    textAlignVertical: "center",
    textAlign: "center",
  },
  bookingDetails: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 28,
    marginBottom: screenHeight * 4,
    marginVertical: 20,
  },
  textCoupon: {
    color: "white",
    fontSize: 15,
    fontWeight: "bold",
    textAlign: "center",
    textAlignVertical: "center",
  },
  textInput: {
    borderWidth: 1.5,
    borderRadius: 10,
    paddingLeft: 10,
    fontSize: 17,
    marginTop: screenHeight * 2,
  },
  modalText: {
    marginTop: screenHeight * 1,
    // textAlignVertical: "center",
    fontSize: 18,
    marginLeft: "13%",
    alignSelf: "center",
    fontWeight: 400,
  },

  totalText: {
    marginTop: screenHeight * 2,
    marginTop: screenHeight * 2,
    fontSize: 25,
    fontWeight: "bold",
  },
  right: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 2,
    fontSize: 18,
    alignSelf: "center",
  },

  lessonPrice: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 5,
    fontSize: 18,
  },

  lessonPrice1: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 4,
    fontSize: 18,
    alignSelf: "center",
  },
  total: {
    marginTop: screenHeight * 2.5,
    // marginLeft: screenWidth * 15,
    fontSize: 20,
  },
  lessonSummary: {
    fontWeight: "bold",
    fontSize: 18,
    alignSelf: "center",
    marginTop: screenHeight * -0.5,
    marginBottom: screenHeight,
  },
  price: {
    marginTop: screenHeight * 3,
    fontSize: 18,
    alignSelf: "center",
    fontWeight: "bold",
  },
});

export default App;
