import React, { Component } from "react";
import {
  View,
  Text,
  TextInput,
  Dimensions,
  ScrollView,
  TouchableHighlight,
  Platform,
  BackHandler,
  StyleSheet,
  Image,
} from "react-native";
import FontAwesome from "react-native-vector-icons/FontAwesome";
import * as theme from "./theme1";

import { LinearGradient } from "expo-linear-gradient";
import firebase from "firebase";
var screenHeight = Math.round(Dimensions.get("window").height) / 100;
var screenWidth = Math.round(Dimensions.get("window").width) / 100;
var firebaseConfig = {
  apiKey: "AIzaSyAfGN94rWhA55dceve-ab5R5nEL6o4xXeg",
  authDomain: "new1-930be.firebaseapp.com",
  databaseURL: "https://new1-930be.firebaseio.com",
  projectId: "new1-930be",
  storageBucket: "new1-930be.appspot.com",
  messagingSenderId: "332990256430",
  appId: "1:332990256430:web:640a6413492c34bf2a96bf",
  measurementId: "G-SBPS6449GM",
};
const { width } = Dimensions.get("window");
const styles = StyleSheet.create({
  flex: {
    flex: 0.5,
    padding: 5,
  },
  column: {
    flexDirection: "column",
  },
  row: {
    flexDirection: "row",
  },
  header: {
    backgroundColor: theme.colors.white,
    paddingHorizontal: theme.sizes.padding,
    paddingTop: theme.sizes.padding * 1.33,
    paddingBottom: theme.sizes.padding * 0.66,
    justifyContent: "space-between",
    alignItems: "center",
  },
  articles: {},
  destinations: {
    flex: 1,
    justifyContent: "space-between",
    paddingBottom: 30,
  },
  destination: {
    width: width - theme.sizes.padding * 2,
    height: width * 0.6,
    marginHorizontal: theme.sizes.margin,
    paddingHorizontal: theme.sizes.padding,
    paddingVertical: theme.sizes.padding * 0.66,
    borderRadius: theme.sizes.radius,
  },
  destinationInfo: {
    position: "absolute",
    borderRadius: theme.sizes.radius,
    paddingHorizontal: theme.sizes.padding,
    paddingVertical: theme.sizes.padding / 2,
    bottom: 20,
    left: (width - theme.sizes.padding * 4) / (Platform.OS === "ios" ? 3.2 : 3),
    backgroundColor: theme.colors.white,
    width: width - theme.sizes.padding * 4,
  },
  recommendedHeader: {
    justifyContent: "space-between",
    alignItems: "flex-end",
    paddingHorizontal: theme.sizes.padding,
  },
  recommendation: {
    width: (width - theme.sizes.padding * 2) / 7,
    height: (width - theme.sizes.padding * 2) / 100,
    marginHorizontal: 8,
    backgroundColor: theme.colors.white,
    borderRadius: theme.sizes.radius,
    marginVertical: theme.sizes.margin * 12.5,
  },
  recommendationImage: {
    width: (width - theme.sizes.padding * 2) / 8,
    height: (width - theme.sizes.padding * 2) / 8,
  },
  avatar: {
    width: theme.sizes.padding,
    height: theme.sizes.padding,
    borderRadius: theme.sizes.padding / 2,
  },
  rating: {
    fontSize: theme.sizes.font * 2,
    color: theme.colors.white,
    fontWeight: "bold",
  },
  shadow: {
    shadowColor: theme.colors.black,
    shadowOffset: {
      width: 0,
      height: 6,
    },
    shadowOpacity: 0.05,
    shadowRadius: 10,
    elevation: 5,
  },
});
export default class Signup extends Component {
  renderRatings(rating) {
    const stars = new Array(5).fill(0);
    return stars.map((_, index) => {
      const activeStar = Math.floor(rating) >= index + 1;
      return (
        <FontAwesome
          name="star"
          key={`star-${index}`}
          size={theme.sizes.font}
          color={theme.colors[activeStar ? "active" : "gray"]}
        />
      );
    });
  }

  render() {
    if (true) {
      return (
        <LinearGradient
          colors={["#09C6F9", "#045DE9"]}
          start={{ x: 1, y: 0 }}
          end={{ x: 0, y: 1 }}
          style={{ height: screenHeight * 100 }}
        >
          <View
            style={[
              styles.flex,
              styles.column,
              styles.recommendation,
              styles.shadow,
            ]}
          >
            <View style={[styles.flex]}>
              <Image
                style={[styles.recommendationImage]}
                source={{
                  uri:
                    "https://images.unsplash.com/photo-1507501336603-6e31db2be093?auto=format&fit=crop&w=800&q=80",
                }}
              />
            </View>
            <View
              style={[
                styles.flex,
                styles.column,
                styles.shadow,
                {
                  justifyContent: "space-evenly",
                  padding: theme.sizes.padding / 2,
                },
              ]}
            >
              <Text
                style={{
                  marginBottom: -25,
                  marginTop: -20,
                  fontSize: theme.sizes.font * 1.25,
                  fontWeight: "500",
                }}
              >
                odihfowhief
              </Text>
              <Text style={{ color: "black", fontWeight: "400" }}>
                jdsnksdn
              </Text>
              <View
                style={[
                  styles.row,
                  {
                    alignItems: "center",
                    justifyContent: "space-between",
                  },
                ]}
              >
                {this.renderRatings(4.3)}
                <Text style={{ color: theme.colors.active }}>4.3</Text>
              </View>
            </View>
          </View>
        </LinearGradient>
      );
    } else {
      return <View />;
    }
  }
}
