import React, { Component } from "react";
import {
  Text,
  View,
  StyleSheet,
  Image,
  TouchableOpacity,
  Platform,
} from "react-native";
import Markdown from "react-native-markdown-renderer";
import { Colors, Layout, FontSizes } from "./constants";
import { ScrollView, RectButton } from "react-native-gesture-handler";
import Constants from "expo-constants";
import { View as AnimatableView } from "react-native-animatable";
import { RegularText, BoldText, SemiBoldText } from "./components/StyledText";
const ClipBorderRadius = ({ children, style }) => {
  return (
    <View
      style={[{ borderRadius: 3, overflow: "hidden", marginTop: 10 }, style]}
    >
      {children}
    </View>
  );
};

export default class HomePage extends Component {
  render() {
    return (
      <View>
        <View style={styles.headerContainer}>
          <View style={styles.headerRowSpeaker}>
            <View style={styles.headerColumnSpeaker}>
              <TouchableOpacity onPress={() => console.log("lsjng")}>
                <Image
                  source={{
                    uri:
                      "https://static.toiimg.com/photo/msid-71935935/71935935.jpg",
                  }}
                  style={styles.avatarMultiple}
                />
              </TouchableOpacity>

              <View style={{ alignItems: "center" }}>
                <TouchableOpacity onPress={() => console.log("sdfn")}>
                  <SemiBoldText style={styles.headerText}>
                    Hetan Thakkar
                  </SemiBoldText>
                  <RegularText style={styles.headerText}>
                    @HetanThakkar
                  </RegularText>
                  <BoldText style={styles.talkTitleText}>
                    How i fucked up everything
                  </BoldText>
                </TouchableOpacity>
              </View>
            </View>
          </View>
        </View>
        <AnimatableView
          animation="fadeIn"
          useNativeDriver
          delay={Platform.OS === "ios" ? 50 : 150}
          duration={500}
          style={styles.content}
        >
          <View>
            <SemiBoldText style={styles.sectionHeader}>Bio</SemiBoldText>
            <Markdown>
              Open source fanatic, technical founder, maker of LiteTimer,
              co-author of react-spring, co-author of immer, co-maintainer of
              react-native-macos, and author of vana.
            </Markdown>
          </View>
        </AnimatableView>
        <ClipBorderRadius>
          <RectButton
            style={styles.bigButton}
            onPress={this._handlePressJobUrl}
            underlayColor="#fff"
          >
            <SemiBoldText style={styles.bigButtonText}>Work with</SemiBoldText>
          </RectButton>
        </ClipBorderRadius>
      </View>
    );
  }
}
const styles = StyleSheet.create({
  container: {},
  bigButton: {
    backgroundColor: Colors.blue,
    paddingHorizontal: 15,
    height: 50,
    marginHorizontal: 15,
    alignItems: "center",
    justifyContent: "center",
    borderRadius: BORDER_RADIUS,
    overflow: "hidden",
    flexDirection: "row",
  },
  avatar: {
    width: 100,
    height: 100,
    borderRadius: 50,
    marginBottom: 10,
  },
  avatarMultiple: {
    width: 90,
    alignSelf: "center",
    height: 90,
    borderRadius: 500,
    marginTop: 10,
    marginBottom: 10,
  },
  content: {
    backgroundColor: "#fff",
    paddingBottom: 20,
    paddingHorizontal: 20,
  },
  markdownBio: {
    backgroundColor: "#fff",
    paddingBottom: 20,
    paddingHorizontal: 20,
    width: 300,
    height: 200,
  },
  bigButtonText: {
    fontSize: FontSizes.normalButton,
    color: "#fff",
    textAlign: "center",
  },
  markdownTalkDescription: {
    backgroundColor: "#fff",
    paddingBottom: 20,
    paddingHorizontal: 20,
    width: 300,
    height: 600,
  },
  headerRowSpeaker: {
    flexDirection: "row",
    height: 140,
  },
  headerColumnSpeaker: {
    alignItems: "center",
    justifyContent: "center",
    paddingHorizontal: 5,
  },
  headerContainer: {
    backgroundColor: Colors.blue,
    paddingTop: Constants.statusBarHeight + Layout.notchHeight + 20,
    paddingBottom: 20,
    paddingHorizontal: 20,
    alignItems: "center",
    justifyContent: "center",
  },
  headerText: {
    color: "#fff",
    textAlign: "center",
    fontSize: FontSizes.subtitle,
  },
  talkTitleText: {
    color: "#fff",
    fontSize: FontSizes.title,
    textAlign: "center",
    marginTop: 10,
  },
  sectionHeader: {
    fontSize: FontSizes.bodyTitle,
    marginTop: 15,
    marginBottom: 3,
  },
  videoWrapper: {},
});
