import React, { Component } from "react";
import {
  View,
  Text,
  ScrollView,
  TextInput,
  RectButton,
  StyleSheet,
  TouchableHighlight,
  Dimensions,
  ActivityIndicator,
  BackHandler,
} from "react-native";
import Constants from "expo-constants";
import { LinearGradient } from "expo-linear-gradient";
import * as Font from "expo-font";
import firebase from "firebase";
var screenHeight = Math.round(Dimensions.get("window").height) / 100;
var screenWidth = Math.round(Dimensions.get("window").width) / 100;
var firebaseConfig = {
  apiKey: "AIzaSyAfGN94rWhA55dceve-ab5R5nEL6o4xXeg",
  authDomain: "new1-930be.firebaseapp.com",
  databaseURL: "https://new1-930be.firebaseio.com",
  projectId: "new1-930be",
  storageBucket: "new1-930be.appspot.com",
  messagingSenderId: "332990256430",
  appId: "1:332990256430:web:640a6413492c34bf2a96bf",
  measurementId: "G-SBPS6449GM",
};
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

export default class Signup extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  componentDidMount() {
    if (!Constants.platform.web.ua.includes("Windows")) {
      this.setState({ multiply: 2 });
      this.setState({ orientation: "column" });
    }
    if (Constants.platform.web.ua.includes("Windows")) {
      this.setState({ width: screenWidth * 45 });
    } else {
      this.setState({ width: screenWidth * 90 });
    }
    if (Constants.platform.web.ua.includes("Windows")) {
      this.setState({ width1: screenWidth * 27 });
    } else {
      this.setState({ width1: screenWidth * 5 });
    }
    if (Constants.platform.web.ua.includes("Windows")) {
      this.setState({ width2: screenWidth * 13 });
    } else {
      this.setState({ width2: screenWidth * 45 });
    }
    if (Constants.platform.web.ua.includes("Windows")) {
      this.setState({ width3: screenWidth * -0.5 });
    } else {
      this.setState({ width3: screenWidth * -50 });
    }
    if (Constants.platform.web.ua.includes("Windows")) {
      this.setState({ width4: screenWidth * 15 });
    } else {
      this.setState({ width4: screenWidth * 50 });
    }
  }
  loadFonts() {
    return Font.loadAsync({
      "Poppins-ExtraLight": require("./assets/fonts/Poppins-ExtraLight.ttf"),
      "Poppins-Medium": require("./assets/fonts/Poppins-Medium.ttf"),
      "Poppins-Bold": require("./assets/fonts/Poppins-Bold.ttf"),
    });
  }
  componentWillUnmount() {
    BackHandler.removeEventListener("hardwareBackPress", this.handleBackButton);
  }
  render() {
    if (true) {
      return (
        <ScrollView style={styles.centeredView}>
          <LinearGradient
            // colors={["#fc00ff", "#00dbde"]}
            colors={["#09C6F9", "#045DE9"]}
            // colors={["#B621FE", "#1FD1F9"]}
            start={{ x: 1, y: 0 }}
            end={{ x: 0, y: 1 }}
            style={{ height: screenHeight * 100 }}
          >
            <Text
              style={{
                color: "#FFFFFF",
                fontSize: 32,
                fontWeight: "bold",
                marginTop: 40,
                marginBottom: 8,
                textAlign: "center",
              }}
            >
              Booking Details
            </Text>
            <Text style={styles.lessonSummary}>Lesson Summary</Text>
          </LinearGradient>
        </ScrollView>
      );
    } else {
      return <View />;
    }
  }
}
const styles = StyleSheet.create({
  centeredView: {
    flex: 1,
    alignSelf: "center",
    marginTop: screenHeight * 7,
    width: screenWidth * 90,
    height: screenHeight,
  },
  modalView: {
    backgroundColor: "white",
    borderRadius: 20,
    borderWidth: 0.9,
    padding: screenHeight * 4,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.95,
    shadowRadius: 10,
    elevation: 8,
  },
  pay: {
    backgroundColor: "#fc0f84",
    borderRadius: 20,
    padding: 10,
    width: screenWidth * 60,
    height: screenHeight * 7,
    alignSelf: "center",
    marginTop: screenHeight * 2,
  },
  applied: {
    marginTop: screenHeight * 2,
    fontSize: 16,
    color: "green",
    marginLeft: screenWidth * 2,
  },
  openButton: {
    backgroundColor: "#F194FF",
    borderRadius: 20,
    padding: 10,
    width: screenWidth * 70,
    height: screenHeight * 6,
    alignSelf: "center",
    marginTop: screenHeight * 5,
  },

  coupon: {
    backgroundColor: "#F194FF",
    borderRadius: 5,
    width: screenWidth * 25,
    height: screenHeight * 4,
    marginTop: screenHeight * 2,
  },
  textStyle: {
    color: "white",
    fontSize: 20,
    fontWeight: "bold",
    textAlignVertical: "center",
    textAlign: "center",
  },
  bookingDetails: {
    textAlign: "center",
    fontWeight: "bold",
    fontSize: 28,
    marginBottom: screenHeight * 2,
  },
  textCoupon: {
    color: "white",
    fontSize: 15,
    fontWeight: "bold",
    textAlign: "center",
    textAlignVertical: "center",
  },
  textInput: {
    borderWidth: 1.5,
    borderRadius: 10,
    paddingLeft: 10,
    fontSize: 17,
    marginTop: screenHeight * 2,
  },
  modalText: {
    marginTop: screenHeight * 2,
    textAlignVertical: "center",
    fontSize: 18,
  },

  totalText: {
    marginTop: screenHeight * 2,
    marginTop: screenHeight * 2,
    fontSize: 25,
    fontWeight: "bold",
  },
  right: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 2,
    fontSize: 18,
  },

  lessonPrice: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 5,
    fontSize: 18,
  },

  lessonPrice1: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 3,
    fontSize: 18,
  },
  total: {
    marginTop: screenHeight * 2,
    marginLeft: screenWidth * 15,
    fontSize: 20,
  },
  lessonSummary: {
    fontWeight: "bold",
    fontSize: 18,
    marginTop: screenHeight * -0.5,
    marginBottom: screenHeight,
  },
  price: {
    marginTop: screenHeight * 3,
    fontSize: 18,
    fontWeight: "bold",
  },
});
