import React from "react";
import { Image, StyleSheet, View, Text } from "react-native";
import FadeIn from "react-native-fade-in-image";
import * as Device from "expo-device";
import { BoldText, SemiBoldText, RegularText } from "../components/StyledText";
import { RectButton } from "react-native-gesture-handler";
export default class SpeakerRow extends React.Component {
  componentDidMount() {
    console.log(Device.brand);
  }
  render() {
    //   const {item: speaker} = this.props;
    return (
      <RectButton
        onPress={this._handlePress}
        activeOpacity={0.05}
        style={{ backgroundColor: "#fff" }}
      >
        <View style={styles.row}>
          <View style={styles.rowAvatarContainer}>
            <FadeIn>
              <Image
                source={{
                  uri:
                    "https://townsquare.media/site/252/files/2020/03/emma-watson.jpg",
                }}
                style={{ width: 50, height: 50, borderRadius: 25 }}
              />
            </FadeIn>
          </View>
          <View style={{ flexDirection: "row" }}>
            <View style={styles.rowData}>
              <Text style={{ fontSize: 20 }}>Hetan iusdniufno</Text>
              <RegularText>React Native</RegularText>
            </View>
            <View style={styles.rowData}>
              <BoldText>1:45</BoldText>
            </View>
          </View>
        </View>
      </RectButton>
    );
  }
}
const styles = StyleSheet.create({
  row: {
    padding: 10,
    borderBottomWidth: StyleSheet.hairlineWidth,
    borderColor: "#eee",
    flexDirection: "row",
  },
  rowAvatarContainer: {
    paddingVertical: 5,
    paddingRight: 10,
    paddingLeft: 0,
  },
  rowData: {
    flex: 0.95,
  },
});
